const API_BASE_URL = 'http://localhost:8080/api/expenses';

const ExpenseService = {
  async getExpenses() {
    try {
      console.log('Fetching expenses from:', API_BASE_URL);
      const response = await fetch(API_BASE_URL);
      console.log('GET Response status:', response.status);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log('GET Response data:', data);
      return data;
    } catch (error) {
      console.error('Error fetching expenses:', error);
      throw error;
    }
  },

  async deleteExpense(id) {
    try {
      const url = `${API_BASE_URL}/${id}`;
      console.log('DELETE Request to:', url);

      const response = await fetch(url, {
        method: 'DELETE',
      });

      console.log('DELETE Response status:', response.status);
      console.log('DELETE Response headers:', response.headers);

      if (!response.ok) {
        const errorText = await response.text();
        console.error('DELETE Error response:', errorText);
        throw new Error(`HTTP error! status: ${response.status}, message: ${errorText}`);
      }

      // For DELETE, we might not get JSON back
      try {
        const data = await response.json();
        console.log('DELETE Response JSON:', data);
        return data;
      } catch (jsonError) {
        console.log('DELETE Response has no JSON, returning success');
        return { success: true };
      }
    } catch (error) {
      console.error('Error deleting expense:', error);
      throw error;
    }
  },
};

export default ExpenseService;
